import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  ShieldCheck, 
  Users, 
  MessageSquare, 
  PoundSterling, 
  Package, 
  Building,
  ArrowRight,
  LucideIcon
} from "lucide-react";
import hrLogoPath from "@assets/HR_1754837463723.png";
import cqcLogoPath from "@assets/cqc transparent_1754837827860.png";
import messagingLogoPath from "@assets/Screenshot 2025-08-10 at 16.15.57_1754839009063.png";
import moneyLogoPath from "@assets/Gemini_Generated_Image_ouh17aouh17aouh1_1754840637280.jpeg";
import stockLogoPath from "@assets/Screenshot 2025-08-10 at 16.48.12_1754840917179.png";
import facilitiesLogoPath from "@assets/Screenshot 2025-08-10 at 16.59.22_1754841619562.png";

interface Module {
  id: string;
  title: string;
  name: string;
  description: string;
  icon: string;
  status: 'good' | 'attention' | 'critical';
}

const iconMap: Record<string, LucideIcon> = {
  'shield-check': ShieldCheck,
  'users': Users,
  'message-square': MessageSquare,
  'pound-sterling': PoundSterling,
  'package': Package,
  'building': Building,
};

const statusColors = {
  good: 'bg-medical-green',
  attention: 'bg-amber-500',
  critical: 'bg-alert-red'
};

const moduleColors = {
  cqc: 'bg-chiron-blue',
  hr: 'bg-chiron-blue', 
  messaging: 'bg-chiron-blue',
  money: 'bg-chiron-orange',
  stock: 'bg-chiron-orange',
  facilities: 'bg-chiron-blue'
};

export default function ModulesGrid() {
  const { data: modules, isLoading } = useQuery<Module[]>({
    queryKey: ['/api/modules']
  });

  if (isLoading) {
    return (
      <div className="grid grid-cols-3 gap-6">
        {Array.from({ length: 6 }).map((_, i) => (
          <Card key={i} className="p-6">
            <div className="flex items-center justify-between mb-4">
              <Skeleton className="w-12 h-12 rounded-lg" />
              <Skeleton className="w-3 h-3 rounded-full" />
            </div>
            <Skeleton className="h-6 mb-2" />
            <Skeleton className="h-16 mb-4" />
            <Skeleton className="h-4 w-32" />
          </Card>
        ))}
      </div>
    );
  }

  if (!modules) {
    return (
      <div className="text-center py-12">
        <p className="text-clinical-gray">Failed to load modules</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-3 gap-6">
      {modules.map((module) => {
        const IconComponent = iconMap[module.icon];
        const statusColor = statusColors[module.status];
        const moduleColor = moduleColors[module.name as keyof typeof moduleColors] || 'bg-chiron-blue';

        return (
          <Link key={module.id} href={`/modules/${module.name}`}>
            <Card className="p-6 hover:shadow-lg transition-shadow cursor-pointer group h-full">
              <CardHeader className="p-0 mb-4">
                <div className="flex items-center justify-between">
                  {module.name === 'hr' ? (
                    <img 
                      src={hrLogoPath} 
                      alt="HR Logo" 
                      className="w-12 h-12 object-contain"
                    />
                  ) : module.name === 'cqc' ? (
                    <img 
                      src={cqcLogoPath} 
                      alt="CQC Logo" 
                      className="w-12 h-12 object-contain"
                    />
                  ) : module.name === 'messaging' ? (
                    <img 
                      src={messagingLogoPath} 
                      alt="Messaging Logo" 
                      className="w-12 h-12 object-contain"
                    />
                  ) : module.name === 'money' ? (
                    <img 
                      src={moneyLogoPath} 
                      alt="Money Logo" 
                      className="w-12 h-12 object-contain"
                    />
                  ) : module.name === 'stock' ? (
                    <img 
                      src={stockLogoPath} 
                      alt="Stock Logo" 
                      className="w-12 h-12 object-contain"
                    />
                  ) : module.name === 'facilities' ? (
                    <img 
                      src={facilitiesLogoPath} 
                      alt="Facilities Logo" 
                      className="w-12 h-12 object-contain"
                    />
                  ) : (
                    <div className={`w-12 h-12 ${moduleColor} bg-opacity-10 rounded-lg flex items-center justify-center`}>
                      {IconComponent && (
                        <IconComponent className={`w-6 h-6 ${moduleColor.replace('bg-', 'text-')}`} />
                      )}
                    </div>
                  )}
                  <div className={`w-3 h-3 ${statusColor} rounded-full`}></div>
                </div>
              </CardHeader>
              <CardContent className="p-0">
                <h3 className="text-lg font-semibold text-slate-900 mb-2">{module.title}</h3>
                <p className="text-sm text-clinical-gray mb-4 line-clamp-3">{module.description}</p>
                <div className="flex items-center text-chiron-blue text-sm font-medium group-hover:text-chiron-orange transition-colors">
                  <span>
                    {module.name === 'cqc' && 'Manage Compliance'}
                    {module.name === 'hr' && 'Manage Team'}
                    {module.name === 'messaging' && 'Open Messages'}
                    {module.name === 'money' && 'Manage Finances'}
                    {module.name === 'stock' && 'Manage Stock'}
                    {module.name === 'facilities' && 'Manage Facilities'}
                  </span>
                  <ArrowRight className="w-4 h-4 ml-1" />
                </div>
              </CardContent>
            </Card>
          </Link>
        );
      })}
    </div>
  );
}

client/src/components/staff-management.t.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { ArrowLeft, Plus, Search, Eye, Edit, Trash2 } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import LLMGuide from "@/components/llm-guide";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertStaffSchema, staff } from "@shared/schema";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";
import { createInsertSchema } from "drizzle-zod";
import { useAuth } from "@/components/auth/authProvider";

const staffSchema = createInsertSchema(staff).extend({
  firstName: z.string(),
  lastName: z.string(),
});

type StaffData = z.infer<typeof staffSchema>;

const staffFormSchema = insertStaffSchema
  .extend({
    creator: z.string(),
    practiceId: z.string().optional(),
    // Person fields from insertPersonSchema
    firstName: z.string().min(1, "First name is required"),
    lastName: z.string().min(1, "Last name is required"),
    // Use contract field directly from schema instead of contractType
  })
  .omit({
    // Remove fields that will be handled separately
  });

type StaffFormData = z.infer<typeof staffFormSchema>;

interface StaffManagementProps {
  onBack: () => void;
}

export default function StaffManagement({ onBack }: StaffManagementProps) {
  const { user, logout } = useAuth();
  const [selectedStaff, setSelectedStaff] = useState<StaffData | null>(null);
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [viewMode, setViewMode] = useState<"list" | "view" | "edit">("list");
  const [search, setSearch] = useState<String | null>(null);
  const { toast } = useToast();

  const { data: staff, isLoading } = useQuery<StaffData[]>({
    queryKey: ["/api/hr/staff"],
    queryFn: async () => {
      const response = await apiRequest("GET", "/api/hr/staff", user?.email);
      const result = await response.json();
      return result.data || [];
    },
  });

  const form = useForm<StaffFormData>({
    resolver: zodResolver(staffFormSchema),
    defaultValues: {
      employeeId: "",
      firstName: "",
      lastName: "",
      title: "",
      email: "",
      phone: "",
      address: "",
      dateOfBirth: "",
      niNumber: "",
      position: "admin",
      department: "",
      startDate: "",
      contract: "permanent", // Use contract instead of contractType
      salary: "0",
      workingHours: undefined,
      professionalBody: "",
      professionalBodyNumber: "",
      appraisalDate: "",
      nextAppraisal: "",
      revalidationInfo: "",
      dbsCheckExpiry: "",
      emergencyContactName: "",
      emergencyContactPhone: "",
      emergencyContactRelation: "",
      creator: user?.email,
    },
  });

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(e.currentTarget.value);
  };

  const createStaffMutation = useMutation({
    mutationFn: async (data: StaffFormData) => {
      const response = await apiRequest("POST", "/api/hr/createstaff", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hr/staff"] });
      queryClient.invalidateQueries({ queryKey: ["/api/hr/metrics"] });
      toast({
        title: "Success",
        description: "Staff member added successfully",
      });
      setShowAddDialog(false);
      form.reset();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to add staff member",
        variant: "destructive",
      });
    },
  });

  const updateStaffMutation = useMutation({
    mutationFn: async ({
      employeeId,
      data,
    }: {
      employeeId: string;
      data: Partial<StaffFormData>;
    }) => {
      const response = await apiRequest(
        "PUT",
        `/api/hr/staff/${employeeId}`,
        data,
      );
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hr/staff"] });
      toast({
        title: "Success",
        description: "Staff member updated successfully",
      });
      setViewMode("view");
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to update staff member",
        variant: "destructive",
      });
    },
  });

  const deleteStaffMutation = useMutation({
    mutationFn: async (employeeId: string) => {
      await apiRequest("DELETE", `/api/hr/staff/${employeeId}`, user?.email);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hr/staff"] });
      queryClient.invalidateQueries({ queryKey: ["/api/hr/metrics"] });
      toast({
        title: "Success",
        description: "Staff member deleted successfully",
      });
      setViewMode("list");
      setSelectedStaff(null);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to delete staff member",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: StaffFormData) => {
    if (viewMode === "edit" && selectedStaff) {
      updateStaffMutation.mutate({
        employeeId: selectedStaff.employeeId,
        data,
      });
    } else {
      createStaffMutation.mutate(data);
    }
