import React, { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { apiRequest } from "@/lib/queryClient";
import ChironLogo from "@/lib/logo";
import { Eye, EyeOff, Mail, Lock, User, UserPlus } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

type FormData = z.infer<typeof formSchema>;

interface RegisterFormProps {
  onRegister: (email: string) => void;
  onSwitchToLogin: () => void;
}

const formSchema = z
  .object({
    firstName: z.string().min(1, "First name is required"),
    lastName: z.string().min(1, "Last name is required"),
    email: z.string().email("Please enter a valid email address"),
    password: z.string().min(8, "Password must be at least 6 characters"),
    confirmPassword: z.string().min(8, "Please confirm your password"),
    practiceId: z.string().min(1, {
      message: "Enter practice id.",
    }),
    role: z.enum(["staff", "poweruser", "user"]),
  })
  .refine((data) => data.password === data.confirmPassword, {
    message: "Passwords don't match",
    path: ["confirmPassword"],
  });

export default function RegisterForm({
  onRegister,
  onSwitchToLogin,
}: RegisterFormProps) {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPassword: "",
      practiceId: "",
      role: "user",
    },
  });

  const signUpMutation = useMutation({
    mutationFn: async (data: FormData) => {
      const response = await apiRequest("POST", "/api/signup", data);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Sign Up failed");
      }

      return response.json();
    },
    onSuccess: (data) => {
      localStorage.setItem("token", data.token);
      toast({
        title: "Sign up successful!",
      });
      setLocation("/");
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Uh oh! Something went wrong.",
        description: error.message,
      });
    },
  });

  const onSubmit = (data: FormData) => {
    signUpMutation.mutate(data);
  };

  return (
    <div className="min-h-screen bg-slate-50 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center space-y-2">
          <div className="mx-auto w-12 h-12 bg-blue-600 rounded-lg flex items-center justify-center">
            <ChironLogo />
          </div>
          <CardTitle className="text-2xl font-bold text-slate-900">
            Create Account
          </CardTitle>
          <p className="text-slate-600">Join HR Management System today</p>
        </CardHeader>

        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="firstName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-700">
                        First Name
                      </FormLabel>
                      <FormControl>
                        <div className="relative">
                          <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
                          <Input
                            {...field}
                            placeholder="First name"
                            className="pl-10 border-slate-200 focus:border-blue-500 focus:ring-blue-500"
                          />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="lastName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-700">
                        Last Name
                      </FormLabel>
                      <FormControl>
                        <div className="relative">
                          <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
                          <Input
                            {...field}
                            placeholder="Last name"
                            className="pl-10 border-slate-200 focus:border-blue-500 focus:ring-blue-500"
                          />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-slate-700">
                      Email Address
                    </FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
                        <Input
                          {...field}
                          type="email"
                          placeholder="Enter your email"
                          className="pl-10 border-slate-200 focus:border-blue-500 focus:ring-blue-500"
                        />
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-slate-700">Password</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
                        <Input
                          {...field}
                          type={showPassword ? "text" : "password"}
                          placeholder="Create a password"
                          className="pl-10 pr-10 border-slate-200 focus:border-blue-500 focus:ring-blue-500"
                        />
                        <button
                          type="button"
                          onClick={() => setShowPassword(!showPassword)}
                          className="absolute right-3 top-1/2 transform -translate-y-1/2 text-slate-400 hover:text-slate-600"
                        >
                          {showPassword ? (
                            <EyeOff className="w-4 h-4" />
                          ) : (
                            <Eye className="w-4 h-4" />
                          )}
                        </button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="confirmPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-slate-700">
                      Confirm Password
                    </FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
                        <Input
                          {...field}
                          type={showConfirmPassword ? "text" : "password"}
                          placeholder="Confirm your password"
                          className="pl-10 pr-10 border-slate-200 focus:border-blue-500 focus:ring-blue-500"
                        />
                        <button
                          type="button"
                          onClick={() =>
                            setShowConfirmPassword(!showConfirmPassword)
                          }
                          className="absolute right-3 top-1/2 transform -translate-y-1/2 text-slate-400 hover:text-slate-600"
                        >
                          {showConfirmPassword ? (
                            <EyeOff className="w-4 h-4" />
                          ) : (
                            <Eye className="w-4 h-4" />
                          )}
                        </button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="practiceId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>PracticeId</FormLabel>
                    <FormControl>
                      <Input
                        type="practiceId"
                        placeholder="Enter practice Id"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="role"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Role</FormLabel>
                    <Select
                      onValueChange={field.onChange}
                      defaultValue={field.value}
                    >
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Select a role" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="staff">Staff</SelectItem>
                        <SelectItem value="poweruser">Power User</SelectItem>
                        <SelectItem value="user">User</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button type="submit" disabled={signUpMutation.isPending}>
                {signUpMutation.isPending ? "Signing up..." : "Signed up"}
              </Button>
            </form>
          </Form>

          <div className="mt-6 text-center">
            <p className="text-slate-600">
              Already have an account?{" "}
              <button
                onClick={onSwitchToLogin}
                className="text-blue-600 hover:text-blue-700 font-medium hover:underline"
              >
                Sign in here
              </button>
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/policy.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import {
  ArrowLeft,
  Plus,
  Search,
  Eye,
  Edit,
  Trash2,
  Upload,
  FileText,
} from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import LLMGuide from "@/components/llm-guide";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  insertStaffSchema,
  staff,
  AppraisalEvidence,
  Policy,
  insertAppraisalEvidenceSchema,
} from "@shared/schema";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";
import { createInsertSchema } from "drizzle-zod";
import { FileUploader } from "@/components/FileUploader";
import { useAuth } from "@/components/auth/authProvider";

const staffSchema = createInsertSchema(staff).extend({
  firstName: z.string(),
  lastName: z.string(),
});

type StaffData = z.infer<typeof staffSchema>;

const staffFormSchema = insertStaffSchema
  .extend({
    practiceId: z.string().optional(),
    // Person fields from insertPersonSchema
    firstName: z.string().min(1, "First name is required"),
    lastName: z.string().min(1, "Last name is required"),
    // Use contract field directly from schema instead of contractType
  })
  .omit({
    // Remove fields that will be handled separately
  });

type StaffFormData = z.infer<typeof staffFormSchema>;

interface PolicyManagementProps {
  onBack: () => void;
}

export default function PolicyManagement({ onBack }: PolicyManagementProps) {
  const { user, logout } = useAuth();
  const [selectedStaff, setSelectedStaff] = useState<StaffData | null>(null);
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [viewMode, setViewMode] = useState<"list" | "view" | "edit">("list");
  const [search, setSearch] = useState<String | null>(null);
  const [department, setDepartment] = useState<String | null>(null);
  const { toast } = useToast();

  const { data: staff, isLoading } = useQuery<StaffData[]>({
    queryKey: ["/api/hr/staff", user?.email],
    queryFn: async () => {
      if (!user?.email) throw new Error("Not authenticated");
      const response = await fetch(
        `/api/hr/staff?email=${encodeURIComponent(user.email)}`,
      );
      if (!response.ok) throw new Error("Failed to fetch");
      return await response.json();
    },
    enabled: !!user?.email,
  });

  const { data: appraisals, isLoading: isAppraisalsLoading } = useQuery<
    AppraisalEvidence[]
  >({
    queryKey: ["/api/hr/appraisals", user?.email],
    queryFn: async () => {
      if (!user?.email) throw new Error("Not authenticated");
      const response = await fetch(
        `/api/hr/appraisals?email=${encodeURIComponent(user.email)}`,
      );
      if (!response.ok) throw new Error("Failed to fetch");
      return await response.json();
    },
    enabled: !!user?.email,
  });

  const form = useForm<StaffFormData>({
    resolver: zodResolver(staffFormSchema),
    defaultValues: {
      employeeId: "",
      firstName: "",
      lastName: "",
      title: "",
      email: "",
      phone: "",
      address: "",
      dateOfBirth: "",
      niNumber: "",
      position: "admin",
      department: "",
      startDate: "",
      contract: "permanent", // Use contract instead of contractType
      salary: "0",
      workingHours: undefined,
      professionalBody: "",
      professionalBodyNumber: "",
      appraisalDate: "",
      nextAppraisal: "",
      revalidationInfo: "",
      dbsCheckExpiry: "",
      emergencyContactName: "",
      emergencyContactPhone: "",
      emergencyContactRelation: "",
    },
  });

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(e.currentTarget.value);
  };

  const updateStaffMutation = useMutation({
    mutationFn: async ({
      employeeId,
      data,
    }: {
      employeeId: string;
      data: Partial<StaffFormData>;
    }) => {
      const response = await apiRequest(
        "PUT",
        `/api/hr/staff/${employeeId}`,
        data,
      );
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hr/staff"] });
      toast({
        title: "Success",
        description: "Staff member updated successfully",
      });
      setViewMode("view");
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to update staff member",
        variant: "destructive",
      });
    },
  });

  const uploadAppraisalMutation = useMutation({
    mutationFn: async (evidenceData: {
      fileName: string;
      path: string;
      description?: string;
      employeeId: string;
    }) => {
      const response = await apiRequest(
        "POST",
        "/api/hr/appraisal",
        evidenceData,
      );
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hr/appraisals"] });
      toast({
        title: "Success",
        description: "Appraisal evidence uploaded successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to upload evidence",
        variant: "destructive",
      });
    },
  });

  const handleViewStaff = (staffMember: StaffData) => {
    setSelectedStaff(staffMember);
    setViewMode("view");
  };

  const handleUploadComplete = (filePath: string) => {
    const fileName = prompt("Enter evidence name:");
    const description = prompt("Enter evidence description:");
    const months = prompt("Enter month to next appraisal:");
    if (!selectedStaff) {
      return;
    }

    const evidenceData = {
      fileName: fileName || `Appraisal_${new Date().toLocaleString()}`,
      path: filePath,
      description:
        description ||
        `Appraisal of ${selectedStaff.firstName} ${selectedStaff.lastName} - ${new Date().toLocaleString()}`,
      employeeId: selectedStaff.employeeId,
    };
    var next = new Date();
    next.setMonth(next.getMonth() + parseInt(months || "12"));
    uploadAppraisalMutation.mutate(evidenceData);
    updateStaffMutation.mutate({
      employeeId: selectedStaff.employeeId,

      data: {
        ...selectedStaff,
        appraisalDate: new Date().toISOString().split("T")[0],
        nextAppraisal: next.toISOString().split("T")[0],
      },
    });
  };

  if (viewMode === "view" && selectedStaff) {
    return (
      <div className="min-h-screen bg-slate-50">
        <header className="bg-white border-b border-slate-200 px-6 py-4">
          <div className="max-w-7xl mx-auto flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                onClick={() => setViewMode("list")}
                className="flex items-center space-x-2"
              >
                <ArrowLeft className="w-4 h-4" />
                <span>Back to Staff List</span>
              </Button>
              <div className="w-px h-6 bg-slate-200"></div>
              <h1 className="text-xl font-semibold text-slate-900">
                Staff Profile
              </h1>
            </div>
          </div>
        </header>

        <main className="max-w-7xl mx-auto px-6 py-8">
          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
            <div className="lg:col-span-3">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Contact Information */}
                <Card className="p-6">
                  <h3 className="font-semibold text-slate-900 mb-4">
                    Contact Information
                  </h3>
                  <div className="space-y-3">
                    <div className="flex items-center space-x-4">
                      <div className="w-16 h-16 bg-chiron-blue rounded-full flex items-center justify-center">
                        <span className="text-white font-semibold text-lg">
                          {selectedStaff.firstName[0]}
                          {selectedStaff.lastName[0]}
                        </span>
                      </div>
                      <div>
                        <h4 className="text-lg font-semibold text-slate-900">
                          {selectedStaff.title} {selectedStaff.firstName}{" "}
                          {selectedStaff.lastName}
                        </h4>
                        <p className="text-clinical-gray">
                          {selectedStaff.position}
                        </p>
                      </div>
                    </div>
                    <div className="grid grid-cols-1 gap-3 mt-4">
                      <div className="flex justify-between text-sm">
                        <span className="text-clinical-gray">Email:</span>
                        <span className="text-slate-900">
                          {selectedStaff.email || "Not provided"}
                        </span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span className="text-clinical-gray">Phone:</span>
                        <span className="text-slate-900">
                          {selectedStaff.phone || "Not provided"}
                        </span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span className="text-clinical-gray">Address:</span>
                        <span className="text-slate-900">
                          {selectedStaff.address || "Not provided"}
                        </span>
                      </div>
                    </div>
                  </div>
                </Card>

                {/* Employment Details */}
                <Card className="p-6">
                  <h3 className="font-semibold text-slate-900 mb-4">
                    Employment Details
                  </h3>
                  <div className="space-y-3">
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">Employee ID:</span>
                      <span className="text-slate-900">
                        {selectedStaff.employeeId}
                      </span>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">Department:</span>
                      <span className="text-slate-900">
                        {selectedStaff.department}
                      </span>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">Start Date:</span>
                      <span className="text-slate-900">
                        {selectedStaff.startDate}
                      </span>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">Contract Type:</span>
                      <Badge variant="secondary">
                        {selectedStaff.contract}
                      </Badge>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">Status:</span>
                      <Badge className="bg-medical-green text-white">
                        {selectedStaff.status || "Active"}
                      </Badge>
                    </div>
                  </div>
                </Card>

                {/* Professional Details */}
                <Card className="p-6">
                  <h3 className="font-semibold text-slate-900 mb-4">
                    Professional Compliance
                  </h3>
                  <div className="space-y-3">
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">
                        Professional Body:
                      </span>
                      <span className="text-slate-900">
                        {selectedStaff.professionalBody || "Not applicable"}
                      </span>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">
                        Registration Number:
                      </span>
                      <span className="text-slate-900">
                        {selectedStaff.professionalBodyNumber ||
                          "Not applicable"}
                      </span>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">
                        Next Appraisal:
                      </span>
                      <Button
                        variant="ghost"
                        onClick={() => {
                          const months = prompt(
                            "Enter month to next appraisal:",
                          );
                          if (!selectedStaff) {
                            return;
                          }
                          var next = new Date();
                          next.setMonth(
                            next.getMonth() + parseInt(months || "12"),
                          );
                          updateStaffMutation.mutate({
                            employeeId: selectedStaff.employeeId,

                            data: {
                              ...selectedStaff,
                              nextAppraisal: next.toISOString().split("T")[0],
                            },
                          });
                        }}
                        className="flex items-center space-x-2"
                        disabled={updateStaffMutation.isPending}
                      >
                        <Badge className="bg-medical-green text-white">
                          {selectedStaff.nextAppraisal || "Now"}
                        </Badge>
                      </Button>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-clinical-gray">
                        DBS Check Expiry:
                      </span>
                      <span className="text-slate-900">
                        {selectedStaff.dbsCheckExpiry || "Not provided"}
                      </span>
                    </div>
                  </div>
                </Card>

                <Card className="p-6">
                  <h3 className="font-semibold text-slate-900 mb-4">
                    Add Appraisal
                  </h3>
                  <div className="flex space-x-2">
                    <div>
                      <FileUploader
                        onUploadComplete={handleUploadComplete}
                        maxFileSize={25}
                        acceptedTypes=".pdf,.doc,.docx,.jpg,.jpeg,.png,.xls,.xlsx"
                        disabled={uploadAppraisalMutation.isPending}
                      />
                    </div>
                  </div>
                </Card>

                {appraisals &&
                  appraisals.filter(
                    (appraisal) =>
                      appraisal.employeeId === selectedStaff.employeeId,
                  ).length > 0 && (
                    <Card data-testid="card-uploaded-files">
                      <CardHeader>
                        <CardTitle>Appraisal Evidence</CardTitle>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-2">
                          {appraisals
                            .filter(
                              (appraisal) =>
                                appraisal.employeeId ===
                                selectedStaff.employeeId,
                            )
                            .map((evidence, index) => (
                              <div
                                key={evidence.fileName}
                                className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-800 rounded-lg"
                                data-testid={`uploaded-file-${index}`}
                              >
                                <div className="flex items-center gap-2">
                                  <FileText className="w-4 h-4 text-gray-500" />
                                  <span className="text-sm font-medium">
                                    {evidence.fileName}
                                  </span>
                                  {evidence.description && (
                                    <span className="text-xs text-gray-500 italic">
                                      - {evidence.description}
                                    </span>
                                  )}
                                </div>
                                <span className="text-xs text-gray-500">
                                  {evidence.createdAt
                                    ? new Date(
                                        evidence.createdAt,
                                      ).toLocaleDateString()
                                    : "Recently uploaded"}
                                </span>
                              </div>
                            ))}
                        </div>
                      </CardContent>
                    </Card>
                  )}
              </div>
            </div>

            <div className="lg:col-span-1">
              <LLMGuide
                title="Staff Guide"
                subtitle="Management assistance"
                initialMessage="I can help you with staff records, compliance tracking, and HR policies. What would you like to know about this staff member?"
                placeholder="Ask about staff..."
              />
            </div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      <header className="bg-white border-b border-slate-200 px-6 py-4">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              onClick={onBack}
              className="flex items-center space-x-2"
            >
              <ArrowLeft className="w-4 h-4" />
              <span>Back to HR</span>
            </Button>
            <div className="w-px h-6 bg-slate-200"></div>
            <h1 className="text-xl font-semibold text-slate-900">
              Appraisal Management
            </h1>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          <div className="lg:col-span-3">
            {/* Search and Filters */}
            <Card className="p-6 mb-6">
              <div className="flex space-x-4">
                <div className="flex-1 relative">
                  <Search className="w-4 h-4 absolute left-3 top-3 text-clinical-gray" />
                  <Input
                    placeholder="Search staff members..."
                    className="pl-10"
                    onChange={onChange}
                  />
                </div>
                <Select>
                  <SelectTrigger className="w-48">
                    <SelectValue placeholder="All Departments" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Departments</SelectItem>
                    <SelectItem value="clinical">Clinical</SelectItem>
                    <SelectItem value="administration">
                      Administration
                    </SelectItem>
                    <SelectItem value="management">Management</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </Card>

            {/* Staff Grid */}
            {isAppraisalsLoading || isLoading ? (
              <div className="text-center py-8">
                <p className="text-clinical-gray">Loading staff...</p>
              </div>
            ) : !staff || staff.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-clinical-gray mb-4">
                  No staff members found
                </p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {staff
                  .sort((a, b) => {
                    // Priority order: No date first, then oldest dates
                    if (!a.appraisalDate && !b.appraisalDate) return 0;
                    if (!a.appraisalDate) return -1; // Staff without dates go FIRST
                    if (!b.appraisalDate) return 1; // Staff without dates go FIRST
                    return (
                      new Date(a.appraisalDate).getTime() -
                      new Date(b.appraisalDate).getTime()
                    );
                  })
                  .map((staffMember) => {
                    if (
                      search == null ||
                      staffMember.firstName
                        .toLowerCase()
                        .includes(search.toLowerCase()) ||
                      search == "" ||
                      staffMember.lastName
                        .toLowerCase()
                        .includes(search.toLowerCase()) ||
                      staffMember.employeeId
                        .toLowerCase()
                        .includes(search.toLowerCase())
                    ) {
                      return (
                        <Card key={staffMember.employeeId} className="p-6">
                          <CardContent className="p-0">
                            <div className="flex items-center space-x-4 mb-4">
                              <div className="w-12 h-12 bg-chiron-blue rounded-full flex items-center justify-center">
                                <span className="text-white font-semibold">
                                  {staffMember.firstName[0]}
                                  {staffMember.lastName[0]}
                                </span>
                              </div>
                              <div>
                                <h3 className="font-semibold text-slate-900">
                                  {staffMember.firstName} {staffMember.lastName}
                                </h3>
                                <p className="text-sm text-clinical-gray">
                                  {staffMember.position}
                                </p>
                              </div>
                            </div>
                            <div className="space-y-2 mb-4">
                              <div className="flex justify-between text-sm">
                                <span className="text-clinical-gray">
                                  Employee ID:
                                </span>
                                <span className="text-slate-900">
                                  {staffMember.employeeId}
                                </span>
                              </div>
                              <div className="flex justify-between text-sm">
                                <span className="text-clinical-gray">
                                  Department:
                                </span>
                                <span className="text-slate-900">
                                  {staffMember.department}
                                </span>
                              </div>
                              <div className="flex justify-between text-sm">
                                <span className="text-clinical-gray">
                                  Last Appraisal:
                                </span>
                                <Badge className="bg-medical-green text-white">
                                  {staffMember.appraisalDate || "None"}
                                </Badge>
                              </div>

                              <div className="flex justify-between text-sm">
                                <span className="text-clinical-gray">
                                  Next Appraisal:
                                </span>
                                <Button
                                  variant="ghost"
                                  onClick={() => {
                                    const months = prompt(
                                      "Enter month to next appraisal:",
                                    );

                                    var next = new Date();
                                    next.setMonth(
                                      next.getMonth() +
                                        parseInt(months || "12"),
                                    );
                                    updateStaffMutation.mutate({
                                      employeeId: staffMember.employeeId,

                                      data: {
                                        ...staffMember,
                                        nextAppraisal: next
                                          .toISOString()
                                          .split("T")[0],
                                      },
                                    });
                                  }}
                                  className="flex items-center space-x-2"
                                  disabled={updateStaffMutation.isPending}
                                >
                                  <Badge className="bg-medical-green text-white">
                                    {staffMember.nextAppraisal || "Now"}
                                  </Badge>
                                </Button>
                              </div>
                            </div>
                            <div className="flex space-x-2">
                              <Button
                                variant="outline"
                                size="sm"
                                className="flex-1"
                                onClick={() => handleViewStaff(staffMember)}
                              >
                                <Eye className="w-3 h-3 mr-1" />
                                View Appraisal
                              </Button>
                            </div>
                          </CardContent>
                        </Card>
                      );
                    }
                  })}
              </div>
            )}
          </div>

          <div className="lg:col-span-1">
            <LLMGuide
              title="Staff Guide"
              subtitle="Management assistance"
              initialMessage="I can help you with managing staff appraisals. What would you like to know?"
              placeholder="Ask about staff/appraisals..."
            />
          </div>
        </div>
      </main>
    </div>
  );
}
